var buyLink=document.querySelector(".to-cart");var modalToCart=document.querySelector(".modal-to-cart");var closeModalToCart=modalToCart.querySelector(".close-button");var linkContinue=modalToCart.querySelector(".—Åontinue-shopping");var order=modalToCart.querySelector(".to-order");buyLink.addEventListener("click",function(a){a.preventDefault();modalToCart.classList.add("modal-to-cart-show")});closeModalToCart.addEventListener("click",function(a){a.preventDefault();modalToCart.classList.remove("modal-to-cart-show")});linkContinue.addEventListener("click",function(a){a.preventDefault();modalToCart.classList.remove("modal-to-cart-show")});order.addEventListener("click",function(a){a.preventDefault();modalToCart.classList.remove("modal-to-cart-show")});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(modalToCart.classList.contains("modal-to-cart-show")){modalToCart.classList.remove("modal-to-cart-show")}}});